ValidationRule.errorConditionFormula:
AND(
  NOT(ISBLANK(StartDate__c)),
  OR(
    AND(
      NOT(ISBLANK(Program__r.StartDate__c)),
      StartDate__c < Program__r.StartDate__c
    ),
    AND(
      NOT(ISBLANK(Program__r.EndDate__c)),
      Program__r.EndDate__c < StartDate__c
    )
  )
)
